import unittestfrom nose.tools import *import formencodefrom phanpy import validators as vdef test_openid_validator():    assert_equal(        v.OpenId().to_python('http://google.com/'),        'http://google.com/'    )@raises(formencode.validators.Invalid)def test_openid_validator_invalid_notprovider():    v.OpenId().to_python('http://not-an-openid-provider.com/')@raises(formencode.validators.Invalid)def test_openid_validator_invalid_noturl():    v.OpenId().to_python('definitely not a url')